mapper.sql:
where 1=1

	
		and t.id = #{id}
	
	
		and t.city_code = #{cityCode}
	
	
		and t.park_id = #{parkId}
	
	
		and t.activity_topic_id = #{activityTopicId}
	
	
		and t.activity_name like concat('%',#{activityName},'%') 
	
	
		and t.content = #{content}
	
	
		and t.start_time = #{startTime}
	
	
		and t.end_time = #{endTime}
	
	
		and t.status = #{status}
	
	
		and t.activity_type = #{activityType}
	
	
		and t.activity_imageurl = #{activityImageurl}
	
	
		and t.activity_url = #{activityUrl}
	
	
		and t.activity_preferential_type = #{activityPreferentialType}
	
	
		and t.verifv_status = #{verifvStatus}
	
	
		and t.verifv_user = #{verifvUser}
	
	
		and t.verifv_time = #{verifvTime}
	
	
		and t.vefifv_info = #{vefifvInfo}
	
	
		and t.flag = #{flag}
	
	
		and t.created_by = #{createdBy}
	
	
		and t.updated_by = #{updatedBy}
	
	
		and t.create_time = #{createTime}
	
	
		and t.update_time = #{updateTime}
	
	
		and t.day_ticket_count = #{dayTicketCount}
	
	
		and t.all_ticket_count = #{allTicketCount}

mapper.select:
		SELECT *
		FROM APP_ACTIVITY t
 		WHERE t.id = #{id}

		SELECT t.*,t1.name park_name,top.topic_name,code.activity_code_name
		FROM APP_ACTIVITY t
 		LEFT JOIN app_park t1 ON t.park_id=t1.id
 		LEFT JOIN app_activity_topic top ON t.activity_topic_id=top.id
 		LEFT JOIN app_activity_code code on t.activity_code = code.activity_code	
		where t.flag!='DELETE'
		
			and t.start_time between #{startTime} and #{endTime}
			and t.end_time between #{startTime} and #{endTime}
		
		
			and t.status = #{status}
		
		
			and t.activity_name like concat('%',#{activityName},'%') 
		
		
			order by t.create_time desc
		
		
			${pager.orderCondition}
		
		
			${pager.mysqlQueryCondition}

SELECT * FROM app_activity 
WHERE flag!='DELETE' AND STATUS = 1
  AND city_code = #{cityCode}
  AND activity_name = #{activityName} 
limit 1

select

from APP_ACTIVITY t
where t.city_code = #{cityCode}
and t.park_id = (select id from APP_PARK t1 where t1.park_code = #{parkCode} and t1.city_code = #{cityCode})
and t.activity_type = #{parkType}

SELECT 
 
FROM app_activity t 
WHERE t.city_code = #{cityCode} 
AND t.park_id = #{parkId} 
AND t.status = 1 
AND (t.activity_type = #{activityType} OR t.activity_type = 3)
AND DATE_FORMAT(NOW(),'%y-%m-%d') BETWEEN t.start_time AND t.end_time 
AND t.flag != 'DELETE'
ORDER BY t.start_time DESC;

SELECT * FROM app_activity
where id=#{activityId}
limit 1

SELECT t.* FROM app_activity t
LEFT JOIN app_activity_area_conf t1 ON t.id = t1.obj_id
WHERE 1=1  AND #{dayTime} BETWEEN t.start_time AND t.end_time
AND t1.obj_type = 2 AND t1.area_code = #{areaCode}

	AND t.activity_topic_id = #{activityTopicId}
