mapper.select:
SELECT ba2.area_name area_name,ba1.area_name city_name,t.id,t.user_id,t.over_time,t.status,t.is_departure, t.area_code,t.city_code,t.PARKRECORD_ID,t.park_name,t.plate_no,t.ARRIVAL_TIME, t.prebuy_count,t.departure_time,t.berth_code,t.prebuy_duration,t.photo_count,t.photo_score, t.photo_url,t.flag,t.create_time,t.update_time,u.user_account FROM (SELECT id,user_id,over_time,status,is_departure, area_code,city_code,PARKRECORD_ID,park_name,plate_no,ARRIVAL_TIME, prebuy_count,departure_time,berth_code,prebuy_duration,photo_score,photo_count, photo_url,flag,create_time,update_time FROM app_prebuy_parkrecord WHERE 1=1
and create_time between #{startTime} and #{endTime}
) t left join app_user u on t.user_id = u.id left join base_area ba1 on t.city_code = ba1.area_code left join base_area ba2 on t.area_code = ba2.area_code
order by t.ARRIVAL_TIME desc
${pager.orderCondition}
${pager.mysqlQueryCondition}

SELECT COUNT(1) FROM ( SELECT ba.area_name,t.id,t.user_id,t.over_time,t.status,t.is_departure, t.area_code,t.city_code,t.PARKRECORD_ID,t.park_name,t.plate_no,t.ARRIVAL_TIME, t.prebuy_count,t.departure_time,t.berth_code,t.prebuy_duration,t.photo_count, t.photo_url,t.flag,t.create_time,t.update_time FROM (SELECT id,user_id,over_time,status,is_departure, area_code,city_code,PARKRECORD_ID,park_name,plate_no,ARRIVAL_TIME, prebuy_count,departure_time,berth_code,prebuy_duration,photo_count, photo_url,flag,create_time,update_time FROM app_prebuy_parkrecord WHERE 1=1
and create_Time between #{startTime} and #{endTime}
) t LEFT JOIN base_area ba ON t.area_code=ba.area_code
order by t.ARRIVAL_TIME desc )A

SELECT * FROM app_prebuy_parkrecord 
WHERE 1=1 and flag!= 'DELETE'

	and status = #{status}

and user_id =  #{userId} AND PARKRECORD_ID = #{parkrecordId} 
order by create_time desc
limit 1

SELECT * FROM app_prebuy_parkrecord 
WHERE 1=1 AND flag!= 'DELETE'
AND AREA_CODE = #{areaCode} AND PARKRECORD_ID = #{parkrecordId}

	AND STATUS = #{status}

ORDER BY create_time DESC
LIMIT 1
