SELECT t.refund_pay_fee payFee,t.is_complate,t1.plate_no,t1.payment,t.refund_no,
       t.refund_time,3 order_type 
FROM app_order_refund t
LEFT JOIN app_order_paylog t1 ON t.order_no = t1.order_no 
WHERE t.is_complate = 200 AND t1.is_paid = 200 
  AND t.city_code = #{cityCode}
  AND t1.user_id = #{userId} 
  AND t.refund_no = #{orderNo}
limit 1

SELECT t.refund_fee,t.is_complate,t1.plate_no,t1.payment,t.refund_no,
       t.refund_time,3 order_type 
FROM app_order_refund t
LEFT JOIN app_order_park_monthly t1 ON t.order_no = t1.order_no 
WHERE t.is_complate = 200 AND t1.order_status = 200 
  AND t.city_code = #{cityCode}
  AND t1.user_id = #{userId} 
  AND t.refund_no = #{orderNo}
limit 1

