	SELECT ,b.area_name 
	FROM fanc_order_spms t 
	left join base_area b on b.area_code = t.city_code 
	WHERE t.stat_date >= #{startDate} AND t.stat_date <= #{endDate} 
	
    	and b.id =  #{cityId}
	
