mapper.sql:
	where 1=1

	
	    and t.id = #{id}
	
	
    	and t.city_code =  #{cityCode}
	
	
    	and t.zoom_level =  #{zoomLevel}
	
	
    	and t.longitude_gaode =  #{longitudeGaode}
	
	
    	and t.latitude_gaode =  #{latitudeGaode}
	
	
    	and t.longitude_baidu =  #{longitudeBaidu}
	
	
    	and t.latitude_baidu =  #{latitudeBaidu}
	
	
    	and t.extend_center_longitude_gaode =  #{extendCenterLongitudeGaode}
	
	
    	and t.extend_center_latitude_gaode =  #{extendCenterLatitudeGaode}
	
	
    	and t.extend_center_longitude_baidu =  #{extendCenterLongitudeBaidu}
	
	
    	and t.extend_center_latitude_baidu =  #{extendCenterLatitudeBaidu}
	
	
    	and t.park_count =  #{parkCount}
	
	
    	and t.flag =  #{flag}
	
	
    	and t.create_time =  #{createTime}
	
	
    	and t.update_time =  #{updateTime}

	where 1=1

    
    	and t.ZOOM_LEVEL =  #{scale}
	
	 
    	and t.longitude_baidu  >=  #{minLongitude} and t.longitude_baidu <= #{maxLongitude}
    	and t.latitude_baidu  >=  #{minLatitude} and t.latitude_baidu <= #{maxLatitude} 
	
	 
    	and t.longitude_gaode  >=  #{minLongitude} and t.longitude_gaode <= #{maxLongitude}
    	and t.latitude_gaode  >=  #{minLatitude} and t.latitude_gaode <= #{maxLatitude} 
	
	and t.flag !=  'DELETE'

mapper.select:
	select 
	
	from APP_PARK_AGGREGATION t 
	
	
		order by t.id desc
	
	
      ${pager.orderCondition}
    
     
       ${pager.mysqlQueryCondition}

mapper.sql:
	where 1=1
	
	
    and t.id = #{id}
	
	
	   	and t.city_code =  #{cityCode}
	
	
	   	and t.zoom_level =  #{zoomLevel}
	
	
	   	and t.longitude_gaode =  #{longitudeGaode}
	
	
	   	and t.latitude_gaode =  #{latitudeGaode}
	
	
	   	and t.longitude_baidu =  #{longitudeBaidu}
	
	
	   	and t.latitude_baidu =  #{latitudeBaidu}
	
	
	   	and t.extend_center_longitude_gaode =  #{extendCenterLongitudeGaode}
	
	
	   	and t.extend_center_latitude_gaode =  #{extendCenterLatitudeGaode}
	
	
	   	and t.extend_center_longitude_baidu =  #{extendCenterLongitudeBaidu}
	
	
	   	and t.extend_center_latitude_baidu =  #{extendCenterLatitudeBaidu}
	
	
	   	and t.park_count =  #{parkCount}
	
	
	   	and t.flag =  #{flag}
	
	
	   	and t.create_time =  #{createTime}
	
	
	   	and t.update_time =  #{updateTime}

mapper.select:
SELECT 

FROM APP_PARK_AGGREGATION t
LEFT JOIN BASE_AREA ba ON t.city_code = ba.area_code


	order by t.create_time desc


	${pager.orderCondition}


	${pager.mysqlQueryCondition}
