mapper.sql:
	where 1=1

	
	    and t.id = #{id}
	
	
    	and t.batch_id =  #{batchId}
	
	
    	and t.denomination =  #{denomination}
	
	
    	and t.producer =  #{producer}
	
	
    	and t.prodece_time =  #{prodeceTime}
	
	
    	and t.prodece_count =  #{prodeceCount}
	
	
    	and t.recevicer =  #{recevicer}
	
	
    	and t.receive_time =  #{receiveTime}
	
	
    	and t.valid_date =  #{validDate}
	
	
    	and t.flag =  #{flag}
	
	
    	and t.is_recevice =  #{isRecevice}
	
	
    	and t.is_download =  #{isDownload}

mapper.select:
	select 
	
	from app_member_recharge_card_batch t 
	
	and t.FLAG != 'DELETE' 
	
		ORDER BY t.IS_RECEVICE ASC, PRODECE_TIME desc
	
	
      ${pager.orderCondition}
    
     
       ${pager.mysqlQueryCondition}
