mapper.sql:
	where 1=1

	
	    and t.id = #{id}
	
	
    	and t.monthly_record_id =  #{monthlyRecordId}
	
	
    	and t.user_id =  #{userId}
	
	
    	and t.order_no =  #{orderNo}
	
	
    	and t.bill_no =  #{billNo}
	
	
    	and t.price =  #{price}
	
	
    	and t.payment =  #{payment}
	
	
    	and t.subject =  #{subject}
	
	
    	and t.order_body =  #{orderBody}
	
	
    	and t.order_status =  #{orderStatus}
	
	
    	and t.pay_time =  #{payTime}
	
	
    	and t.buyer_account =  #{buyerAccount}
	
	
    	and t.counter_fee =  #{counterFee}
	
	
    	and t.notify_status =  #{notifyStatus}
	
	
    	and t.notify_time =  #{notifyTime}
	
	
    	and t.created_by =  #{createdBy}
	
	
    	and t.updated_by =  #{updatedBy}
	
	
    	and t.create_time =  #{createTime}
	
	
    	and t.update_time =  #{updateTime}
	
	
    	and t.city_code =  #{cityCode}
	
	
    	and t.area_code =  #{areaCode}

mapper.select:
	select count(1) from app_order_park_monthly t 
	LEFT JOIN base_area t1 ON t.AREA_CODE = t1.area_code
	LEFT JOIN app_park_monthly_record t2 ON t.MONTHLY_RECORD_ID = t2.id
	WHERE 1=1
	
		and t.USER_ACCOUNT = #{userAccount}
	
	
  		AND t.BUYER_ACCOUNT = #{buyerAccount}
    
    
  		AND t.ORDER_NO = #{orderNo}
    
	
  		AND t.NOTIFY_STATUS = #{notifyStatus}
    
    
  		AND t.order_status = #{orderStatus}
    
    
  		AND t.PAYMENT = #{payment}
    
    
  		AND t1.id = #{areaId}
    
    
  		AND t.area_code like '${userCityCode}%'
    
    
  		AND t2.MONTHLY_ID = #{monthlyId}
    
    
  		AND t.CREATE_TIME BETWEEN STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s') 
  		                      AND STR_TO_DATE(#{endTime},'%Y-%m-%d %H:%i:%s')

    SELECT ,t1.area_name,t2.monthly_id 
    FROM app_order_park_monthly t
	LEFT JOIN base_area t1 ON t.AREA_CODE = t1.area_code
	LEFT JOIN app_park_monthly_record t2 ON t.MONTHLY_RECORD_ID = t2.id
	WHERE 1=1
	
		and t.USER_ACCOUNT = #{userAccount}
	
	
  		AND t.BUYER_ACCOUNT = #{buyerAccount}
    
    
  		AND t.ORDER_NO = #{orderNo}
    
	
  		AND t.NOTIFY_STATUS = #{notifyStatus}
    
    
  		AND t.order_status = #{orderStatus}
    
    
  		AND t.PAYMENT = #{payment}
    
    
  		AND t1.id = #{areaId}
    
    
  		AND t.area_code like '${userCityCode}%'
    
    
  		AND t2.MONTHLY_ID = #{monthlyId}
    
    
  		AND t.CREATE_TIME BETWEEN STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s') 
  		                      AND STR_TO_DATE(#{endTime},'%Y-%m-%d %H:%i:%s')
    
	
		ORDER BY t.create_time DESC
	
	
      	${pager.orderCondition}
    
     
      	${pager.mysqlQueryCondition}

mapper.update:
UPDATE app_order_park_monthly 
SET ORDER_STATUS = 400,UPDATED_BY = 'system',UPDATE_TIME = NOW() 
WHERE ORDER_STATUS = 100 AND MONTHLY_RECORD_ID = #{_parameter}
