mapper.sql:
	where 1=1

	
	    and t.id = #{id}
	
	
    	and t.monthly_record_id =  #{monthlyRecordId}
	
	
    	and t.user_id =  #{userId}
	
	
    	and t.start_time =  #{startTime}
	
	
    	and t.end_time =  #{endTime}
	
	
    	and t.monthly_fee =  #{monthlyFee}
	
	
    	and t.accept_time =  #{acceptTime}
	
	
    	and t.verify_user =  #{verifyUser}
	
	
    	and t.verify_time =  #{verifyTime}
	
	
    	and t.verify_status =  #{verifyStatus}
	
	
    	and t.verify_info =  #{verifyInfo}
	
	
    	and t.advice_start_time =  #{adviceStartTime}
	
	
    	and t.advice_monthly_duration =  #{adviceMonthlyDuration}
	
	
    	and t.flag =  #{flag}
	
	
    	and t.created_by =  #{createdBy}
	
	
    	and t.updated_by =  #{updatedBy}
	
	
    	and t.create_time =  #{createTime}
	
	
    	and t.update_time =  #{updateTime}

mapper.select:
SELECT count(1) FROM app_park_monthly_record_log t LEFT JOIN app_park_monthly_record t1 ON t.MONTHLY_RECORD_ID = t1.id LEFT JOIN app_user t2 ON t.user_id = t2.ID WHERE 1=1
AND t.verify_status in (0,1) and t.flag != 'DELETE'
and t.flag = 'DELETE'
and t.verify_status = #{verifyStatus} and t.flag != 'DELETE'
and t2.user_account = #{userAccount}
AND t1.PHONE = #{phone}
AND t1.PLATE_NO = #{plateNo}
AND t1.plate_color = #{plateColor}
AND t1.START_TIME <= str_to_date(#{startTime},'%Y-%m-%d') AND t1.END_TIME >= str_to_date(#{endTime},'%Y-%m-%d')
AND t1.AREA_CODE LIKE '${userCityCode}%'

	SELECT ,t2.USER_ACCOUNT
	FROM app_park_monthly_record_log t
	LEFT JOIN app_park_monthly_record t1 ON t.MONTHLY_RECORD_ID = t1.id 
	LEFT JOIN app_user t2 ON t.user_id = t2.ID
	WHERE 1=1
	
		AND t.verify_status in (0,1) and t.flag != 'DELETE'
	
	
		and t.flag = 'DELETE'
	
	
		and t.verify_status = #{verifyStatus} and t.flag != 'DELETE'
	
	
		and t2.user_account = #{userAccount}
	
	
		 AND t1.PHONE = #{phone}
	
	
		AND t1.PLATE_NO = #{plateNo}
	
	
		AND t1.plate_color = #{plateColor}
	
	
		 AND t1.START_TIME <= str_to_date(#{startTime},'%Y-%m-%d')
		 AND t1.END_TIME >= str_to_date(#{endTime},'%Y-%m-%d')
	
	
		AND t1.AREA_CODE LIKE '${userCityCode}%'
	
	
		ORDER BY t.VERIFY_STATUS ASC,t.create_time DESC,t.update_time DESC
	
	
      ${pager.orderCondition}
    
     
       ${pager.mysqlQueryCondition}

update.foreach:
UPDATE app_park_monthly_record_log
SET VERIFY_STATUS = #{item.verifyStatus},ACCEPT_TIME = #{item.acceptTime},
ACCEPT_USER = #{item.acceptUser},UPDATED_BY = #{item.updatedBy},UPDATE_TIME = #{item.updateTime},
flag = #{item.flag}
WHERE id = #{item.id};

mapper.update:
UPDATE  app_park_monthly_record_log 
SET FLAG = 'DELETE',UPDATED_BY='system',UPDATE_TIME=NOW() 
WHERE MONTHLY_RECORD_ID = #{_parameter}

update.foreach:
UPDATE app_park_monthly_record_log
SET FLAG = 'DELETE',UPDATED_BY = 'system',UPDATE_TIME = now()
WHERE MONTHLY_RECORD_ID = #{item.monthlyRecordId};
