mapper.sql:
	where 1=1

	
	    and t.id = #{id}
	
	
    	and t.topic_name like '%${topicName}%'
	
	
    	and t.topic_abstract =  #{topicAbstract}
	
	
    	and t.start_time =  #{startTime}
	
	
    	and t.end_time =  #{endTime}
	
	
    	and t.status =  #{status}
	
	
    	and t.displayconf =  #{displayconf}
	
	
    	and t.topic_imageurl =  #{topicImageurl}
	
	
    	and t.topic_url =  #{topicUrl}
	
	
    	and t.created_by =  #{createdBy}
	
	
    	and t.updated_by =  #{updatedBy}
	
	
    	and t.create_time =  #{createTime}
	
	
    	and t.update_time =  #{updateTime}
	
	
    	and t.order_num =  #{orderNum}

mapper.select:
select count(1) from app_activity_topic t
where 1=1

	and t.start_time between #{startTime} and #{endTime}
	and t.end_time between #{startTime} and #{endTime}


	and t.start_time >= #{startTime}


	and t.end_time <= #{endTime}


	and t.topic_name like '%${topicName}%'


	and t.status =  #{status}


	and t.displayconf =  #{displayconf}

	select 
	
	from app_activity_topic t 
	where 1=1
	
		and t.start_time between #{startTime} and #{endTime}
		and t.end_time between #{startTime} and #{endTime}
	
	
		and t.start_time >= #{startTime}
	
	
		and t.end_time <= #{endTime}
	
	
    	and t.topic_name like '%${topicName}%'
	
	
    	and t.status =  #{status}
	
	
    	and t.displayconf =  #{displayconf}
	
	
		order by t.order_num asc
	
	
      ${pager.orderCondition}
    
     
       ${pager.mysqlQueryCondition}
