mapper.sql:
	where 1=1

	
	    and t.id = #{id}
	
	
    	and t.message_topic_name =  #{messageTopicName}
	
	
    	and t.message_topic_desc =  #{messageTopicDesc}
	
	
    	and t.topic_type =  #{topicType}
	
	
    	and t.displayconf =  #{displayconf}
	
	
    	and t.is_common =  #{isCommon}
	
	
    	and t.order_num =  #{orderNum}
	
	
    	and t.created_by =  #{createdBy}
	
	
    	and t.updated_by =  #{updatedBy}
	
	
    	and t.create_time =  #{createTime}
	
	
    	and t.update_time =  #{updateTime}

mapper.select:
	select 
	
	from app_message_topic t 
	
	
		order by t.order_num asc,t.id desc
	
	
      ${pager.orderCondition}
    
     
       ${pager.mysqlQueryCondition}
