mapper.sql:
	where 1=1

	
	    and t.id = #{id}
	
	
    	and t.resource_name =  #{resourceName}
	
	
    	and t.app_type =  #{appType}
	
	
    	and t.resource_filename =  #{resourceFilename}
	
	
    	and t.resource_url =  #{resourceUrl}
	
	
    	and t.resource_desc =  #{resourceDesc}
	
	
    	and t.flag =  #{flag}
	
	
    	and t.created_by =  #{createdBy}
	
	
    	and t.updated_by =  #{updatedBy}
	
	
    	and t.create_time =  #{createTime}
	
	
    	and t.update_time =  #{updateTime}
	
	
		and t.city_code LIKE '${userCityCode}%'
	
	
		and t.resource_type = #{resourceType}

mapper.select:
select  
 from app_resource t 
 where t.id = #{id}

	
		${pager.oracleQueryConditionStart}
	
	select 
	
	from app_resource t 
	
	
		order by t.create_time desc
	
	
      ${pager.orderCondition}
    
	
		${pager.oracleQueryConditionEnd}
