mapper.sql:
	where 1=1

	
	    and t.id = #{id}
	
	
    	and t.activity_id =  #{activityId}
	
	
    	and t.city_code =  #{cityCode}
	
	
    	and t.activity_name =  #{activityName}
	
	
    	and t.area_name =  #{areaName}
	
	
    	and t.discount_name =  #{discountName}
	
	
    	and t.payment =  #{payment}
	
	
    	and t.fulluse =  #{fulluse}
	
	
    	and t.discount =  #{discount}
	
	
    	and t.maxdiscount_fee =  #{maxdiscountFee}
	
	
    	and t.is_overly =  #{isOverly}
	
	
    	and t.discount_start_time =  #{discountStartTime}
	
	
    	and t.discount_end_time =  #{discountEndTime}
	
	
    	and t.discount_desc =  #{discountDesc}
	
	
    	and t.user_day_count =  #{userDayCount}
	
	
    	and t.day_count =  #{dayCount}
	
	
    	and t.all_count =  #{allCount}
	
	
    	and t.flag =  #{flag}
	
	
    	and t.created_by =  #{createdBy}
	
	
    	and t.updated_by =  #{updatedBy}
	
	
    	and t.create_time =  #{createTime}
	
	
    	and t.update_time =  #{updateTime}

mapper.select:
SELECT COUNT(1) FROM app_activity_discount_conf t LEFT JOIN app_activity t1 ON t.activity_id=t1.id where 1=1
and t1.activity_name LIKE '%${activityName}%'
and t.payment = #{payment}

	
		${pager.oracleQueryConditionStart}
	
 	SELECT t.activity_id, t.city_code, t.discount_name, t.payment, t.fulluse, t.discount, t.maxdiscount_fee, t.is_overly, 
 		   t.discount_start_time, t.discount_end_time, t.discount_desc, t.user_day_count, t.day_count, t.all_count, t.flag, t.created_by,
           t.updated_by, t.create_time, t.update_time, t.id ,t1.activity_name
    FROM app_activity_discount_conf t
    LEFT JOIN app_activity t1 ON t.activity_id=t1.id
	where 1=1
	
		and t1.activity_name  LIKE '%${activityName}%'
	
	
		and t.payment = #{payment}
	




	
		order by t.create_time desc
	
	
      ${pager.orderCondition}
    
    
		${pager.oracleQueryConditionEnd}
