mapper.sql:
	where 1=1

	
	    and t.id = #{id}
	
	
    	and t.provider_no =  #{providerNo}
	
	
    	and t.appid =  #{appid}
	
	
    	and t.payment =  #{payment}
	
	
    	and t.seller_account =  #{sellerAccount}
	
	
    	and t.provider_key =  #{providerKey}
	
	
    	and t.alipay_public =  #{alipayPublic}
	
	
    	and t.alipay_private =  #{alipayPrivate}
	
	
    	and t.is_used =  #{isUsed}
	
	
    	and t.is_default =  #{isDefault}
	
	
    	and t.validity_date =  #{validityDate}
	
	
    	and t.notify_url =  #{notifyUrl}
	
	
    	and t.flag =  #{flag}
	
	
    	and t.create_time =  #{createTime}
	
	
    	and t.created_by =  #{createdBy}
	
	
    	and t.update_time =  #{updateTime}
	
	
    	and t.updated_by =  #{updatedBy}

mapper.select:
    
		${pager.oracleQueryConditionStart}
	
	select 
	
	from app_provider_connect t 
	left join app_provider ap on t.provider_no = ap.provider_no
	
	AND t.is_default=0
	
		order by t.create_time desc
	
	
      ${pager.orderCondition}
    
	
		${pager.oracleQueryConditionEnd}

SELECT 
 
FROM app_provider_connect t
WHERE t.provider_no = #{providerNo}
AND t.is_used = 1
AND t.flag != 'DELETE'
ORDER BY t.is_default DESC , t.payment ASC;
