mapper.sql:
	where 1=1

	
	    and t.id = #{id}
	
	
    	and t.city_code =  #{cityCode}
	
	
    	and t.message_name like '%${messageName}%'
	
	
    	and t.content =  #{content}
	
	
    	and t.message_type =  #{messageType}
	
	
    	and t.push_range =  #{pushRange}
	
	
    	and t.flag =  #{flag}
	
	
    	and t.created_by =  #{createdBy}
	
	
    	and t.updated_by =  #{updatedBy}
	
	
    	and t.create_time =  #{createTime}
	
	
    	and t.update_time =  #{updateTime}
	
	
    	and t.expire_date =  #{expireDate}

mapper.select:
SELECT t.*,ba.area_name area_name FROM app_message t
 LEFT JOIN BASE_AREA ba 
 ON t.city_code=ba.area_code where t.id = #{id}

${pager.oracleQueryConditionStart}
SELECT C.id, ba.area_name AS area_name,C.city_code, C.message_name, C.message_type, C.push_range, C.created_by, C.updated_by, C.CREATE_TIME, C.UPDATE_TIME,C.scale FROM (SELECT t.id, t.city_code, t.message_name, t.message_type, t.push_range, t.created_by, t.updated_by, t.CREATE_TIME, t.UPDATE_TIME, CASE WHEN B.scale != '' THEN B.scale ELSE 0 END scale FROM app_message t LEFT JOIN (SELECT A.message_id, FLOOR(ROUND(A.times / A.totalTimes, 2) * 100) AS scale FROM (SELECT * FROM (SELECT message_id, COUNT(1) times FROM app_user_message GROUP BY message_id ORDER BY create_time DESC) mc, (SELECT COUNT(1) totalTimes FROM app_user_message) aumc) A) B ON t.id = B.message_id) C, BASE_AREA ba where 1=1
and C.message_name like '%${messageName}%'
and C.city_code = #{cityCode}
and C.message_type != 4
and C.message_type = #{messageType}
and C.city_code LIKE '${userCityCode}%'
AND ba.flag!='DELETE' and C.city_code = ba.area_code
order by C.create_time desc
${pager.orderCondition}
${pager.oracleQueryConditionEnd}

select 
count(t.ID)
from app_message t 
where t.FLAG!='DELETE' 
and ((t.CITY_CODE=#{cityCode} and t.PUSH_RANGE=#{userType}) or (t.user_id = #{userId} and t.PUSH_RANGE = 100))  
and not EXISTS
(select id from app_user_message um where um.MESSAGE_ID=t.id and um.IS_READ=1 
	and (um.DEVICE_NO= #{deviceNo}
    
    	 and um.user_id=#{userId}    
	
    )
)

SELECT * FROM ( SELECT A.*, ROWNUM RN FROM (
select m.id, m.city_code, m.message_name, m.content, m.message_type, m.expire_date, um.MESSAGE_ID, m.create_time from app_message m left join app_user_message um on um.MESSAGE_ID=m.id and (um.DEVICE_NO= #{deviceNo}
and um.user_id=#{userId}
) and um.IS_READ=1 where 1=1 and ((m.PUSH_RANGE=#{userType} and m.CITY_CODE=#{cityCode}) or (m.user_id = #{userId} and m.PUSH_RANGE = 100)) and m.FLAG!='DELETE' order by m.create_time desc
) A WHERE ROWNUM <=(#{startNum}+#{pageSize})) where RN > #{startNum}
