mapper.sql:
	where 1=1

	
	    and t.id = #{id}
	
	
    	and t.day_time =  to_date('${dayTime}','yyyy-MM-dd')
	
	
    	and t.city_code =  #{cityCode}
	
	
    	and t.area_code =  #{areaCode}
	
	
    	and t.activity_name =  #{activityName}
	
	
    	and t.activity_id =  #{activityId}
	
	
    	and t.putlog_count =  #{putlogCount}
	
	
    	and t.used_count =  #{usedCount}
	
	
    	and t.unused_user_count =  #{cumulativUnusedUserCount}
	
	
    	and t.part_user_count =  #{cumulativeParticipateUserCount}
	
	
    	and t.payfee_order_count =  #{cumulativePayfeeOrderCount}
	
	
    	and t.payfee_user_count =  #{payfeeUserCount}
	
	
    	and t.tp_user_count =  #{ticketpayUserCount}
	
	
    	and t.ticket_money_amount =  #{ticketMoneyAmount}
	
	
    	and t.act_discount_amount =  #{actualDiscountAmount}
	
	
    	and t.ticket_discount_scale =  #{ticketDiscountScale}
	
	
    	and t.revenue_growth_rate =  #{revenueGrowthRate}
	
	
    	and t.create_time =  #{createTime}

mapper.select:
    
		${pager.oracleQueryConditionStart}
	
	select 
	
	from app_stat_activity_effect t 
	
	
		order by t.id desc
	
	
      ${pager.orderCondition}
    
	
		${pager.oracleQueryConditionEnd}

SELECT * FROM app_stat_activity_effect t LEFT JOIN app_activity t1 ON t.activity_id = t1.id WHERE t.day_time = to_date('${dayTime}','yyyy-MM-dd')
and t1.activity_topic_id = #{activityTopicId}
AND t.area_code like '${areaCode}%'

  SELECT COUNT(t.activity_id) putlog_count FROM app_activity_ticket_putlog t
  LEFT JOIN app_user_ext t1 ON t.user_id = t1.user_id
  WHERE to_char(t.GET_TICKET_TIME,'yyyy-MM-dd') = #{dayTime} 
    AND (t1.city_code LIKE '${areaCode}%' OR t1.city_code IS NULL)
	AND t.activity_id  = #{activityId}

SELECT COUNT(t.activity_id) used_count FROM app_order_paylog t
WHERE to_char(t.create_time,'yyyy-MM-dd') = #{dayTime}  
  AND t.is_paid = 200 AND t.activity_type = 1
  AND t.city_code = #{areaCode}
  AND t.activity_id  = #{activityId}

SELECT COUNT(t.activity_id) payfee_order_count
FROM app_order_paylog t
WHERE t.activity_type = 1 AND is_paid = 200
  AND t.city_code = #{areaCode}
  AND t.activity_id  = #{activityId}

SELECT COUNT(DISTINCT t.user_id) payfee_user_count FROM app_order_paylog t
WHERE t.is_paid = 200 
  AND to_char(t.create_time,'yyyy-MM-dd') = #{dayTime} 
  AND t.city_code = #{areaCode} 
  AND t.activity_id  = #{activityId}

SELECT COUNT(DISTINCT t.user_id) tp_user_count FROM app_order_paylog t
WHERE  t.activity_type = 1 AND t.is_paid = 200
   AND to_char(t.create_time,'yyyy-MM-dd') = #{dayTime}  
   AND t.city_code = #{areaCode}
   AND t.activity_id  = #{activityId}

SELECT SUM(CASE WHEN t.ticket_money = 0 THEN t.ticket_fee ELSE t.ticket_money END) ticket_money_amount 
FROM app_order_paylog t
WHERE t.is_paid = 200 AND t.activity_type = 1
  AND to_char(t.create_time,'yyyy-MM-dd') = #{dayTime} 
  AND t.city_code = #{areaCode}
  AND t.activity_id  = #{activityId}

SELECT SUM(t.ticket_fee) act_discount_amount
FROM app_order_paylog t
WHERE t.is_paid = 200 AND t.activity_type = 1
  AND to_char(t.create_time,'yyyy-MM-dd') = #{dayTime}   
  AND t.city_code = #{areaCode}
  AND t.activity_id  = #{activityId}

SELECT ROUND(SUM(t.ticket_fee)/SUM(t.park_fee)*100,2) ticket_discount_scale
FROM app_order_paylog t
WHERE t.is_paid = 200 AND t.activity_type = 1 
AND to_char(t.create_time,'yyyy-MM-dd') = #{dayTime} 
AND t.city_code = #{areaCode}
AND t.activity_id  = #{activityId}

SELECT SUM(t.pay_fee) FROM app_order_paylog t
WHERE  t.is_paid = 200 
   AND t.city_code = #{areaCode}
   AND to_char(t.create_time,'yyyy-MM-dd') BETWEEN #{startDate} AND #{endDate}

${pager.oracleQueryConditionStart}
SELECT t.*,t1.area_name area_name,t2.area_name city_name FROM app_stat_activity_effect t LEFT JOIN base_area t1 ON t.area_code=t1.area_code LEFT JOIN base_area t2 ON t.city_code=t2.area_code WHERE 1=1 AND t1.flag!='DELETE'
and t.day_time between to_date('${startDate}','yyyy-MM-dd') and to_date('${endDate}','yyyy-MM-dd')
AND t.AREA_CODE like '${areaCode}%'
and t.AREA_CODE LIKE '${userCityCode}%'
order by t.day_time desc,t.area_code asc
${pager.orderCondition}
${pager.oracleQueryConditionEnd}
