mapper.sql:
	where 1=1

	
	    and t.id = #{id}
	
	
    	and t.day_time =  #{dayTime}
	
	
    	and t.city_code =  #{cityCode}
	
	
    	and t.tran_success_count =  #{tranSuccessCount}
	
	
    	and t.tran_success_amount =  #{tranSuccessAmount}
	
	
    	and t.tran_fault_count =  #{tranFaultCount}
	
	
    	and t.tran_fault_amount =  #{tranFaultAmount}
	
	
    	and t.book_count =  #{bookCount}
	
	
    	and t.book_amount =  #{bookAmount}
	
	
    	and t.should_paid_amount =  #{shouldPaidAmount}
	
	
    	and t.actually_paid_amount =  #{actuallyPaidAmount}
	
	
    	and t.create_time =  #{createTime}

mapper.select:
    
		${pager.oracleQueryConditionStart}
	
	select 
	
	from STAT_TRANSACTION_DAILY t 
	
	
		order by t.create_time desc
	
	
      ${pager.orderCondition}
    
	
		${pager.oracleQueryConditionEnd}

select count(1) from( SELECT odr.CITY_CODE,odr.AREA_CODE,ba1.area_name city_name,ba2.area_name area_name,to_char(odr.CREATE_TIME,'yyyy-MM-dd'), COUNT(1) tranTotalCount, SUM(PRICE) tranTotalAmount, SUM(CASE WHEN odr.ORDER_STATUS=200 THEN 1 ELSE 0 END ) tranSuccessCount, SUM(CASE WHEN odr.ORDER_STATUS=200 AND odr.PAY_TYPE=1 THEN PRICE ELSE 0 END ) aliPaySuccessAmount, SUM(CASE WHEN odr.ORDER_STATUS=200 AND odr.PAY_TYPE=2 THEN PRICE ELSE 0 END ) wxSuccessAmount, SUM(CASE WHEN odr.ORDER_STATUS=200 THEN PRICE ELSE 0 END ) tranSuccessAmount, SUM(CASE WHEN odr.ORDER_STATUS!=200 THEN 1 ELSE 0 END ) tranFaultCount, SUM(CASE WHEN odr.ORDER_STATUS!=200 THEN PRICE ELSE 0 END ) tranFaultAmount, SUM(CASE WHEN odr.ORDER_STATUS=200 AND LENGTH(odr.PARKRECORD_ID)>10 THEN 1 ELSE 0 END ) bookCount, SUM(CASE WHEN odr.ORDER_STATUS=200 AND LENGTH(odr.PARKRECORD_ID)>10 THEN PRICE ELSE 0 END ) bookAmount FROM app_order odr LEFT JOIN BASE_AREA ba1 ON odr.city_code = ba1.area_code LEFT JOIN BASE_AREA ba2 ON odr.area_code = ba2.area_code GROUP BY odr.CITY_CODE,odr.AREA_CODE,to_char(odr.CREATE_TIME,'yyyy-MM-dd'),ba1.area_name,ba2.area_name having 1=1
and odr.AREA_CODE = #{areaCode}
and to_char(odr.CREATE_TIME,'yyyy-MM-dd') = '${dayTime}'
and odr.AREA_CODE LIKE '${userCityCode}%'
) a

${pager.oracleQueryConditionStart}
SELECT odr.CITY_CODE,odr.AREA_CODE,ba1.area_name city_name,ba2.area_name area_name,to_char(odr.CREATE_TIME,'yyyy-MM-dd'), COUNT(1) tranTotalCount, SUM(PRICE) tranTotalAmount, SUM(CASE WHEN odr.ORDER_STATUS=200 THEN 1 ELSE 0 END ) tranSuccessCount, SUM(CASE WHEN odr.ORDER_STATUS=200 AND odr.PAY_TYPE=1 THEN PRICE ELSE 0 END ) aliPaySuccessAmount, SUM(CASE WHEN odr.ORDER_STATUS=200 AND odr.PAY_TYPE=2 THEN PRICE ELSE 0 END ) wxSuccessAmount, SUM(CASE WHEN odr.ORDER_STATUS=200 THEN PRICE ELSE 0 END ) tranSuccessAmount, SUM(CASE WHEN odr.ORDER_STATUS!=200 THEN 1 ELSE 0 END ) tranFaultCount, SUM(CASE WHEN odr.ORDER_STATUS!=200 THEN PRICE ELSE 0 END ) tranFaultAmount, SUM(CASE WHEN odr.ORDER_STATUS=200 AND LENGTH(odr.PARKRECORD_ID)>10 THEN 1 ELSE 0 END ) bookCount, SUM(CASE WHEN odr.ORDER_STATUS=200 AND LENGTH(odr.PARKRECORD_ID)>10 THEN PRICE ELSE 0 END ) bookAmount FROM app_order odr LEFT JOIN BASE_AREA ba1 ON odr.city_code = ba1.area_code LEFT JOIN BASE_AREA ba2 ON odr.area_code = ba2.area_code GROUP BY odr.CITY_CODE,odr.AREA_CODE,to_char(odr.CREATE_TIME,'yyyy-MM-dd'),ba1.area_name,ba2.area_name having 1=1
and odr.AREA_CODE = #{areaCode}
and to_char(odr.CREATE_TIME,'yyyy-MM-dd') = '${dayTime}'
and odr.AREA_CODE LIKE '${userCityCode}%'
ORDER BY to_char(odr.CREATE_TIME,'yyyy-MM-dd') DESC
${pager.orderCondition}
${pager.oracleQueryConditionEnd}
